import React from 'react';
import { View, Image, Text } from 'react-native';
import { RectButton } from 'react-native-gesture-handler';

import styles from './styles';

import heartOutlineIcon from '../../assets/images/icons/heart-outline.png';
import unfavoriteIcon from '../../assets/images/icons/unfavorite.png';
import whatsappIcon from '../../assets/images/icons/whatsapp.png';

const TeacherItem: React.FC = () => {
  return (
    <View style={styles.container}>
      <View style={styles.profile}>
        <Image 
          source={{uri: 'https://avatars0.githubusercontent.com/u/15193959?s=460&u=e1ebb36beebe175ec47e43a947781d389c8c1b9a&v=4'}} 
          style={styles.avatar}
        />

        <View style={styles.profileInfo}>
          <Text style={styles.name}>Daniel Klotz</Text>
          <Text style={styles.subject}>Programming</Text>
        </View>
      </View>

      <Text style={styles.bio}>
        Programming teacher with more than 4 years of experience
        always learning new stuf to teach to you
      </Text>

      <View style={styles.footer}>
        <Text style={styles.price}>
          Price/Class {'   '}
          <Text style={styles.priceValue}>
            80 â‚¬
          </Text>
        </Text>

        <View style={styles.buttonsContainer}>
          <RectButton style={[styles.favoriteButton, styles.favorited]}>
            {/* <Image source={heartOutlineIcon}/> */}
            <Image source={unfavoriteIcon}/>
          </RectButton>

          <RectButton style={styles.contactButton}>
            <Image source={whatsappIcon}/>
            <Text style={styles.contactButtonText}>
              Get in touch
            </Text>
          </RectButton>
        </View>
      </View>
    </View>
  );
}

export default TeacherItem;
